var Node = function(type, start, end) {
	this.type = type;
	this.start = start;
	this.end = end;
	this.children = [];
}

Node.prototype.add = function(node) {
	this.children.push(node);
}

Node.prototype.addList = function(nodes) {
	this.children.push.apply(this.children, nodes);
}



var NodeInstancier = function(type, offset, text) {
	return new Node(type, offset, offset + text.length)
}



exports.Node = Node;
exports.NodeInstancier = NodeInstancier;