var prelude = require('prelude-ls');



// -----------------------------------------------------------------------------

var methodsUsed = [
	'add',
	'addList',
	'flag',
	'set'
]



// -----------------------------------------------------------------------------

function Node() {
	this.childrenIndex = {id: null};
}

function empty() {}
prelude.forEach(function(method) {
	Node.prototype[method] = empty;
}, methodsUsed);



// -----------------------------------------------------------------------------

exports.Node = Node;
