var Node = function(type, start, end) {
	this.type = type;
	this.start = start;
	this.end = end;

	this.children = [];
	this.properties = {};
}

Node.prototype.add = function(id, node) {
	this.children.push(node);
	// this.children.push({id: id, node: node});
}

Node.prototype.addList = function(id, nodes) {
	this.children.push.apply(this.children, nodes);
	// for (var i = 0, length = nodes.length; i < length; i++) {
	// 	this.children.push({id: id, node: nodes[i]});
	// }
}

Node.prototype.set = function(key, value) {
	this.properties[key] = value;
}

exports.Node = Node;
