var oop = require('oop').oop;



var Position = oop.Class({
	name: 'Position',

	schema: {
		inputToSpec: {
			'Number': 'index'
		},
		properties: [
			{names: ['index', 'offset', 'position', 'pos', 'character', 'char']},
			{names: ['line', 'ln', 'l', 'L']},
			{names: ['column', 'col', 'c', 'C']}
		]
	}
});



exports.Position = Position;
