var Logger = require('tester').logger.Logger;

var Suite = require('./suite').Suite;



var $logger = new Logger();


// -----------------------------------------------------------------------------

$logger.sep('First test');
// $logger.on();

var suite = Suite({
	name: 'First suite test',
	description: 'This is a simple test to know if my suite class works',

	benchmarks: [
		{
			name: 'First',
			fn: function() {
				return 5;
			}
		}, {
			name: 'Second',
			fn: function() {
				return 5;
			}
		}
	]
});

$logger.log(suite);

$logger.del();
var result = suite.run(true);
$logger.del();
$logger.log(result);

// -----------------------------------------------------------------------------

$logger.sep('Exception');
$logger.on();
$logger.on();

var suite = Suite({
	name: 'Exception',

	benchmarks: [
		{
			name: 'First',
			fn: function() {
				return 5;
			}
		}, {
			name: 'Second',
			fn: function() {
				throw 'Second function failed!';
			}
		}, {
			name: 'Third',
			fn: function() {
				return 5;
			}
		}
	]
});

$logger.del();
var result = suite.run(true);
$logger.del();
$logger.log(result);
